package pack1;

public class R1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			for (int i=0;i<=5;i++) {
				char c = (char)('A'+i);
				for(int j=5;j>=i;j--) {
				System.out.print(c);
			}
			System.out.println();
			}

			//AAAAAA
			//BBBBB
			//CCCC
			//DDD
			//EE
			//F
	}

}
