package pack1;

public class R4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=5;i>=0;i--) {
			for(int j=0;j<=i;j++) {
				char c=(char)('A'+j);
				System.out.print(c);
			}
			System.out.println();
		}

	}
	//ABCDEF
	//ABCDE
	//ABCD
	//ABC
	//AB
	//A


}
