package pack1;

public class R5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=5;i>=0;i--) {
			for(int j=0;j<=i;j++) {
				System.out.print(i);
			}
			System.out.println();
		}

	}
	//555555
	//44444
	//3333
	//222
	//11
	//0

}
